package com.wemakeprice.parsetest.util;

import com.fasterxml.jackson.databind.ObjectMapper;
import com.wemakeprice.parsetest.exception.BaseException;
import com.wemakeprice.parsetest.exception.ParseException;
import com.wemakeprice.parsetest.model.parse.ParseData;
import com.wemakeprice.parsetest.model.parse.ReturnData;
import com.wemakeprice.parsetest.model.response.RESPONSE_STATUS;
import com.wemakeprice.parsetest.service.ParseService;
import org.hamcrest.collection.IsMapContaining;
import org.junit.jupiter.api.DisplayName;
import org.junit.jupiter.api.Test;
import org.mockito.InjectMocks;
import org.mockito.Mock;

import java.util.Map;

import static org.hamcrest.MatcherAssert.assertThat;
import static org.junit.jupiter.api.Assertions.*;

@DisplayName("파싱 유틸리티 테스트")
public class ParseUtilTest {
    @Mock
    BaseException baseException;

    @Mock
    ParseException parseException;

    private static final String url = "https://docs.oracle.com/javase/8/docs/api/help-doc.html";
    private static final String errorUrl = "http://error";

    private static final String html_alpha = "";
    private static final String html_number = "00112233888899";

    private static final String text_alpha = "";
    private static final String text_number = "000000000011111111111111111122222222222222222222222222222222222223333333344445578888888999";

    private static final String html_quotient = "";
    private static final String html_remainder = "";

    private static final String text_quotient = "";
    private static final String text_remainder = "";

    private static final ParseData htmlParseData = ParseData.builder()
            .alpha(html_alpha)
            .number(html_number)
            .build();

    private static final ParseData textParseData = ParseData.builder()
            .alpha(text_alpha)
            .number(text_number)
            .build();

    private static final ReturnData htmlReturnData = ReturnData.builder()
            .quotient(html_quotient)
            .remainder(html_remainder)
            .build();

    private static final ReturnData textReturnData = ReturnData.builder()
            .quotient(text_quotient)
            .remainder(text_remainder)
            .build();

    @Test
    @DisplayName("URL 파싱 테스트")
    public void testParseUrl() throws Exception {
        String parseUrl = ParseUtil.parseUrl(url);

        // 파싱된 html 데이터 길이 체크
        assertTrue(parseUrl.length() > 10130);

        // 데이터 파싱시 에러 없음 확인
        assertDoesNotThrow(() ->
                ParseUtil.parseUrl(url));
    }

    @Test
    @DisplayName("URL 파싱시 요청 URL 정보가 잘못 입력되었을 경우 에러 테스트")
    public void testParseUrlTypeErrorExceptuon() {
        parseException = assertThrows(ParseException.class, () ->
                ParseUtil.parseUrl(errorUrl));
        assertEquals(RESPONSE_STATUS.URL_TYPE_ERROR.getMessage(), parseException.getMessage());
        assertEquals(RESPONSE_STATUS.URL_TYPE_ERROR.getCode(), parseException.getErrorCode());
    }

    @Test
    @DisplayName("몫과 나머지 계산 후 최종 결과값 반환 테스트")
    public void testGetCombineText() {
        ReturnData rd = ParseService.getCombineText(htmlParseData, 1300);

        ObjectMapper objectMapper = new ObjectMapper();
        Map<String, Object> resultMap = objectMapper.convertValue(rd, Map.class);

        // 생성된 몫, 나머지 값이 존재하는지 확인
        assertThat(resultMap, IsMapContaining.hasKey("quotient"));
        assertThat(resultMap, IsMapContaining.hasKey("remainder"));

        // 생성된 몫, 나머지 값의 공백값 체크
        assertNotNull(rd.getQuotient());
        assertNotNull(rd.getRemainder());

        // 생성된 몫, 나머지 값이 예상 값과 같은지 비교
        assertEquals(html_quotient, rd.getQuotient());
        assertEquals(html_remainder, rd.getRemainder());
    }

    @Test
    @DisplayName("파싱된 데이터 중 HTML을 제외한 영문자와 숫자 조합 문자열 생성 테스트")
    public void testGetExceptHtml() throws Exception {
        String parseUrl = ParseUtil.parseUrl(url);
        ParseData parseData = ParseService.getExceptHtml(parseUrl);

        ObjectMapper objectMapper = new ObjectMapper();
        Map<String, Object> resultMap = objectMapper.convertValue(parseData, Map.class);

        // 영문자와 숫자 값이 존재하는지 확인
        assertThat(resultMap, IsMapContaining.hasKey("alpha"));
        assertThat(resultMap, IsMapContaining.hasKey("number"));

        // 영문자와 숫자 값이 존재하는지 확인
        assertNotNull(parseData.getAlpha());
        assertNotNull(parseData.getNumber());

        // 영문자와 숫자 값이 같은지 비교
        assertEquals(html_alpha, parseData.getAlpha());
        assertEquals(html_number, parseData.getNumber());
    }

    @Test
    @DisplayName("파싱된 데이터 중 영문자와 숫자 조합 문자열 생성 테스트")
    public void testGetAllText() throws Exception {
        String parseUrl = ParseUtil.parseUrl(url);
        ParseData parseData = ParseService.getAllText(parseUrl);

        ObjectMapper objectMapper = new ObjectMapper();
        Map<String, Object> resultMap = objectMapper.convertValue(parseData, Map.class);

        // 영문자와 숫자 값이 존재하는지 확인
        assertThat(resultMap, IsMapContaining.hasKey("alpha"));
        assertThat(resultMap, IsMapContaining.hasKey("number"));

        // 영문자와 숫자 값이 존재하는지 확인
        assertNotNull(parseData.getAlpha());
        assertNotNull(parseData.getNumber());

        // 영문자와 숫자 값이 같은지 비교
        assertEquals(text_alpha, parseData.getAlpha());
        assertEquals(text_number, parseData.getNumber());
    }
}
